<?php/** * * begin theme setting * * @uses add_theme_page()    * @uses add_action() * * @since paipk1 1.0 * * @return null */function paipk1_ad_theme_setting(){  if($_POST['paipk1_update_themeoptions']=='true') {paipk1_themeoptions_update();}  add_theme_page('theme_setting',__('theme setting','paipk1'), 'administrator', 'theme_setting','paipk1_theme_setting'); }   add_action('admin_menu', 'paipk1_ad_theme_setting');/** * * theme setting begin * * @since paipk1 1.0 * * @return HTML */function paipk1_themeoptions_update(){  if($_POST['if_bootstrap']=='on'){$_POST['if_bootstrap']='checked';} else {$_POST['if_bootstrap']='';}  update_option('paipk1_if_bootstrap', $_POST['if_bootstrap']);  update_option('paipk1_qq', $_POST['qq']);  update_option('paipk1_indexAD2', stripslashes($_POST['indexAD2']));  update_option('paipk1_postAD1', stripslashes($_POST['postAD1']));  update_option('paipk1_postAD2', stripslashes($_POST['postAD2']));  update_option('paipk1_postAD3', stripslashes($_POST['postAD3']));  update_option('paipk1_baidustats', stripslashes($_POST['baidustats']));  update_option('paipk1_statscode', stripslashes($_POST['statscode']));  update_option('paipk1_baidushare', stripslashes($_POST['baidushare']));}/** * * theme setting * * @since paipk1 1.0 * * @return HTML */function paipk1_theme_setting(){ ?>  <div class="wrap">  <h1><?php _e('theme setting','paipk1')?></h1>  <hr>  <form method="POST" action="" style="padding-left:20px;">  <input type="hidden" name="paipk1_update_themeoptions" value="true" /><!-- hidden -->    <p><input type="checkbox" name="if_bootstrap" <?php echo get_option('paipk1_if_bootstrap'); ?> />&nbsp;<?php _e('use out bootstrap','paipk1') ?></p>    <p>QICQ:&nbsp;<input name="qq" type="text" value="<?php echo get_option('paipk1_qq'); ?>">&nbsp;<?php _e('for china','paipk1')?></p>    <h3>广告设置</h3>    <table width="70%" style="text-align:center;">      <tr style="background:#8BC34A;color:#fff">        <th>广告位置</th>        <th>插入代码</th>        <th>使用说明</th>      </tr>      <tr>        <td>文章列表页：内容最后</td>        <td><textarea type="text" name="indexAD2" cols="60" rows="2"><?php echo get_option('paipk1_indexAD2'); ?></textarea></td>        <td>文章列表页面最底部，在底栏上面一点</td>      </tr>      <tr>        <td>文章列表页：底部</td>        <td><textarea type="text" name="indexAD1" cols="60" rows="2"><?php echo get_option('paipk1_indexAD1'); ?></textarea></td>        <td>文章列表页面最底部，在底栏上面一点</td>      </tr>      <tr>        <td>文章页面：顶部</td>        <td><textarea type="text" name="postAD2" cols="60" rows="2"><?php echo get_option('paipk1_postAD2'); ?></textarea></td>        <td>文章标题栏下方，正文上方，<br>可插入代码</td>      </tr>      <tr>        <td>文章页面：内容底部</td>        <td><textarea type="text" name="postAD1" cols="60" rows="2"><?php echo get_option('paipk1_postAD1'); ?></textarea></td>        <td>该代码显示在文章页面上下翻页上方，<br>在移动端口可浏览</td>      </tr>      <tr>        <td>文章页面：最底部</td>        <td><textarea type="text" name="postAD3" cols="60" rows="2"><?php echo get_option('paipk1_postAD3'); ?></textarea></td>        <td>文章页面最底部，在底栏上面一点。</td>      </tr>    </table>    <hr>    <h3>底部自定义代码：</h3>    <p><textarea type="text" name="statscode" cols="60" rows="3"><?php echo get_option('paipk1_statscode'); ?></textarea></p>    <p>可设置底部统计代码，用于统计页面浏览次数。也可以插入其他需要插入的备注说明等。</p>    <hr>    <h3>百度统计代码：</h3>    <p><textarea type="text" name="baidustats" cols="60" rows="3"><?php echo get_option('paipk1_baidustats'); ?></textarea></p>    <p>代码会添加到网站全部页面的/head标签前。</p>    <hr>    <h3>百度分享代码：</h3>    <p><textarea type="text" name="baidushare" cols="60" rows="3"><?php echo get_option('paipk1_baidushare'); ?></textarea></p>    <p>显示在文章"打赏"和"分享"按钮的弹出对话框中。</p>    <hr>        <p><input type="submit" name="submit" id="submit" class="button button-primary" value="<?php _e('Submit','paipk1') ?>"></p>  </form></div>  <?php }   ?>