<?php/** * * begin theme setting * * @uses add_theme_page()    * @uses add_action() * * @since paipk1 1.0 * * @return null */function paipk1_ad_theme_setting(){  if($_POST['paipk1_update_themeoptions']=='true') {paipk1_themeoptions_update();}  add_theme_page('theme_setting',__('Theme Setting','paipk1'), 'administrator', 'theme_setting','paipk1_theme_setting');   if($_POST['paipk1_update_ADoptions']=='true') {paipk1_ADoptions_update();}  add_theme_page('AD_setting',__('AD Setting','paipk1'), 'administrator', 'AD_setting','paipk1_AD_setting');  if($_POST['paipk1_update_PPToptions']=='true') {paipk1_PPToptions_update();}  if($_POST['paipk1_update_Carouseloptions']=='true') {paipk1_Carouseloptions_update();}  add_theme_page('PPT_setting',__('Carousel Setting','paipk1'), 'administrator', 'PPT_setting','paipk1_PPT_setting');}   add_action('admin_menu', 'paipk1_ad_theme_setting');/** * * theme setting * * @since paipk1 1.0 * * @return HTML */function paipk1_themeoptions_update(){  if($_POST['if_bootstrap']=='on'){$_POST['if_bootstrap']='checked';} else {$_POST['if_bootstrap']='';}  update_option('paipk1_if_bootstrap', $_POST['if_bootstrap']);  update_option('paipk1_qq', $_POST['qq']);  update_option('paipk1_baiduStatistics', stripslashes($_POST['baiduStatistics']));  if($_POST['if_SEO']=='on'){$_POST['if_SEO']='checked';} else {$_POST['if_SEO']='';}  update_option('paipk1_if_SEO', $_POST['if_SEO']);  update_option('paipk1_homeKeyword', $_POST['homeKeyword']);  update_option('paipk1_homeDescription', $_POST['homeDescription']);  update_option('paipk1_bottom', stripslashes($_POST['bottom']));  update_option('paipk1_Copyright', $_POST['Copyright']);  update_option('paipk1_baiduShare', stripslashes($_POST['baiduShare']));}function paipk1_theme_setting(){ ?>  <div class="wrap">  <h1><?php _e('Theme Setting','paipk1')?></h1>  <hr>  <form method="POST" action="" style="padding-left:20px;">  <input type="hidden" name="paipk1_update_themeoptions" value="true" /><!-- hidden -->    <p><input type="checkbox" name="if_bootstrap" <?php echo get_option('paipk1_if_bootstrap'); ?> />&nbsp;<?php _e('use out bootstrap','paipk1') ?></p>    <p>QICQ:&nbsp;<input name="qq" type="text" size="10" value="<?php echo get_option('paipk1_qq'); ?>">&nbsp;<?php _e('for china','paipk1')?></p>    <p>      baidu <?php _e('Statistics','paipk1')?>:<br>      <textarea type="text" name="baiduStatistics" cols="60" rows="3"><?php echo get_option('paipk1_baiduStatistics'); ?></textarea>    </p>    <p>      baidu <?php _e('Share','paipk1')?> <?php _e('Code','paipk1')?>:<br>      <textarea type="text" name="baiduShare" cols="60" rows="3"><?php echo get_option('paipk1_baiduShare'); ?></textarea>    </p>    <h3><input type="checkbox" name="if_SEO" <?php echo get_option('paipk1_if_SEO'); ?> />&nbsp;SEO</h3>    <p><?php _e('home Keyword','paipk1')?>:&nbsp;<input name="homeKeyword" type="text" value="<?php echo get_option('paipk1_homeKeyword'); ?>" size="40"></p>    <p><?php _e('home Description','paipk1')?>:<br>      <textarea type="text" name="homeDescription" cols="60" rows="2"><?php echo get_option('paipk1_homeDescription'); ?></textarea>    </p>    <p>    <?php _e('Bottom Setting','paipk1');?>:<br>    <textarea type="text" name="bottom" cols="60" rows="2"><?php echo get_option('paipk1_bottom'); ?></textarea><br>    <?php _e('You can insert the code at the bottom','paipk1');?>    </p>    <p>    <?php _e('Copyright','paipk1');?>:<input name="Copyright" type="text" value="<?php echo get_option('paipk1_Copyright'); ?>" size="50">    </p>    <p><input type="submit" name="submit" id="submit" class="button button-primary" value="<?php _e('Submit','paipk1') ?>"></p>  </form></div>  <?php }/** * * AD setting * * @since paipk1 1.0 * * @return HTML */function paipk1_ADoptions_update(){  update_option('paipk1_BottomAD', stripslashes($_POST['BottomAD']));  update_option('paipk1_CommentAD', stripslashes($_POST['CommentAD']));}function paipk1_AD_setting(){ ?><div class="wrap">  <h1><?php _e('AD Setting','paipk1')?></h1>  <hr>  <form method="POST" action="" style="padding-left:20px;">  <input type="hidden" name="paipk1_update_ADoptions" value="true" /><!-- hidden -->  <p><strong><?php _e('Single Bottom AD','paipk1')?></strong><br>  <textarea type="text" name="BottomAD" cols="60" rows="4"><?php echo get_option('paipk1_BottomAD'); ?></textarea>  </p>  <p><strong><?php _e('Comment Bottom AD','paipk1')?></strong><br>  <textarea type="text" name="CommentAD" cols="60" rows="4"><?php echo get_option('paipk1_CommentAD'); ?></textarea>  </p>  <p><input type="submit" name="submit" id="ADsubmit" class="button button-primary" value="<?php _e('Submit','paipk1') ?>"></p>  </form></div><?php }/** * * PPT setting * * @since paipk1 1.0 * * @return HTML */function paipk1_PPToptions_update(){  require_once(TEMPLATEPATH . '/includes/paipk1-echoHTML.php');}function paipk1_Carouseloptions_update(){  if($_POST['if_Carousel']=='on'){$_POST['if_Carousel']='checked';} else {$_POST['if_Carousel']='';}  update_option('paipk1_if_Carousel', $_POST['if_Carousel']);  if(is_numeric($_POST['indexTopOne'])){    update_option('paipk1_indexTopOne', $_POST['indexTopOne']);  }else{    update_option('paipk1_indexTopOne', '');  }}function paipk1_PPT_setting(){ ?><div class="wrap">  <h1><?php _e('Carousel Setting','paipk1')?></h1>  <hr>  <form method="POST" action="" style="padding-left:20px;">    <p><input type="checkbox" name="if_Carousel" <?php echo get_option('paipk1_if_Carousel'); ?> />&nbsp;<?php _e('open the Carousel,and you can TOP one single of ID','paipk1') ?> <input name="indexTopOne" type="text" value="<?php echo get_option('paipk1_indexTopOne'); ?>" size="5"></p>    <input type="hidden" name="paipk1_update_Carouseloptions" value="true" /><!-- hidden -->    <p><input type="submit" name="submit" id="CarouselSubmit" class="button button-primary" value="<?php _e('Submit','paipk1') ?>"></p>  </form>  <hr><input type="submit" value="<?php _e('addTable','paipk1')?>" id="addTable" class="button button-primary"/> <input type="submit" value="<?php _e('deleteTable','paipk1')?>" id="deleteTable" class="button button-primary"/><br><br>  <form method="POST" action="" style="padding-left:20px;">  <table width="70%" style="text-align:center">    <thead>      <tr>        <th width="4%"><?php _e('option','paipk1')?></th>        <th width="32%"><?php _e('Picture URL','paipk1')?>(*)</th>        <th width="32%"><?php _e('Link address','paipk1')?>(*)</th>        <th width="32%"><?php _e('Picture name','paipk1')?></th>      </tr>    </thead>    <tbody id="PPT"><?php  if(is_file(TEMPLATEPATH .'/includes/plugin/alltr.html')) {    include TEMPLATEPATH .'/includes/plugin/alltr.html';  }?>    </tbody>  </table>  <input type="hidden" name="paipk1_update_PPToptions" value="true" /><!-- hidden -->  <p><input type="submit" name="submit" id="PPTsubmit" class="button button-primary" value="<?php _e('Submit','paipk1') ?>"></p>  </form></div><script type='text/javascript' src='<?php bloginfo('template_url'); ?>/js/jquery.min.js'></script><script type='text/javascript' src='<?php bloginfo('template_url'); ?>/js/addTable.min.js'></script><?php }?>