<?php/** * paipk1_widget_Comment() * * @since paipk1 1.0 * * @return echo $output */	global $wpdb;	$sql = "SELECT DISTINCT ID, post_title, post_password, comment_ID,comment_post_ID, comment_author,comment_author_email,comment_date_gmt, comment_approved, comment_type,comment_author_url, SUBSTRING(comment_content,1,17) AS com_excerpt FROM $wpdb->comments LEFT OUTER JOIN $wpdb->posts ON ($wpdb->comments.comment_post_ID = $wpdb->posts.ID) WHERE comment_approved = '1' AND comment_type = '' AND post_password = '' ORDER BY comment_date_gmt DESC LIMIT 10";	$comments = $wpdb->get_results($sql);	$output = $pre_HTML;	foreach ($comments as $comment) {	$output .= "\n<li class='new'>".get_avatar($comment, 32)."<a rel='external nofollow' target='_blank' href=\"". $comment->comment_author_url ."\" >" . $comment->comment_author . "</a> : <br /> <a target='_blank' href=\"" . get_permalink($comment->ID) . "#comment-" . $comment->comment_ID . "\" title=\"". $comment->comment_author. ":" . $comment->post_title . "\">" . strip_tags($comment->com_excerpt) ."</a></li>";	}	$output .= $post_HTML;	echo $output;?>