<?php/** * paipk1_widget_Comment() * * @since paipk1 1.0 * * @return echo $output */	global $wpdb;	$sql = "SELECT DISTINCT ID, post_title, post_password, comment_ID,comment_post_ID, comment_author,comment_author_email,comment_date_gmt, comment_approved, comment_type,comment_author_url, SUBSTRING(comment_content,1,17) AS com_excerpt FROM $wpdb->comments LEFT OUTER JOIN $wpdb->posts ON ($wpdb->comments.comment_post_ID = $wpdb->posts.ID) WHERE comment_approved = '1' AND comment_type = '' AND post_password = '' ORDER BY comment_date_gmt DESC LIMIT 4";	$comments = $wpdb->get_results($sql);	$output = '<section class="widget" id="divComments"><div class="modulename"><h5 class="glyphicon">'.__('Comment','paipk1').__('List','paipk1').'</h5></div><div><ul>';	foreach ($comments as $comment) {		$output .= "<li><a rel='nofollow' target='_blank' class='img-comment' title='".$comment->post_title ."' href=\"".get_permalink($comment->ID)."\" >".get_avatar($comment, 40)."</a>";		$output .= "<li><a rel='nofollow' target='_blank' title='".$comment->comment_author."' href=\"".get_permalink($comment->ID)."\" >".$comment->comment_author."</a>";		$output .= "<small class='small'> - ".$comment->comment_date_gmt."</small><br />";		$output .= strip_tags($comment->com_excerpt);	}		$output .= '</ul></div></section>';		echo $output;?>